// frontend/src/pages/Register.js
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../AppStyles.css";
import { API_BASE } from "../config";

function Register() {
  const [form, setForm] = useState({ username: "", email: "", password: "" });
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const userData = { ...form, role: "customer" }; // default role
      const res = await axios.post(`${API_BASE}/api/register`, userData);
      localStorage.setItem("token", res.data.token);
      localStorage.setItem("role", res.data.user.role);
      setMessage("Registration successful! Redirecting...");
      setTimeout(() => navigate("/dashboard"), 1000);
    } catch (err) {
      setMessage("Registration failed: " + (err.response?.data?.msg || err.message));
    }
  };

  return (
    <div className="page-container">
      <div className="card">
        <h2 className="title">Create an Account</h2>
        <p className="subtitle">Register to access your secure banking dashboard.</p>

        <form className="form-layout" onSubmit={handleRegister}>
          <div className="form-group">
            <label className="form-label">Username</label>
            <input
              className="form-input"
              name="username"
              placeholder="Enter your username"
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label className="form-label">Email</label>
            <input
              className="form-input"
              type="email"
              name="email"
              placeholder="Enter your email"
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label className="form-label">Password</label>
            <input
              className="form-input"
              type="password"
              name="password"
              placeholder="Enter your password"
              onChange={handleChange}
              required
            />
          </div>

          <button className="primary-button" type="submit">
            Register
          </button>
        </form>

        {message && <p className="message-text">{message}</p>}
      </div>
    </div>
  );
}

export default Register;
