import { useEffect, useState } from "react";
import axios from "axios";
import "../AppStyles.css";
import { API_BASE } from "../config";

function Dashboard() {
  const [payments, setPayments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchPayments = async () => {
      try {
        const res = await axios.get(`${API_BASE}/api/payments`, {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
        });

        const data = Array.isArray(res.data) ? res.data : res.data?.payments;
        setPayments(data || []);
      } catch (err) {
        setError(err.response?.data?.msg || "Failed to fetch payments");
      } finally {
        setLoading(false);
      }
    };
    fetchPayments();
  }, []);

  return (
    <div className="dashboard-container">
      <div className="dashboard-card">
        <h2 className="dashboard-title">ðŸ’¸ Payment Dashboard</h2>
        <p className="dashboard-subtitle">
          Track and manage your international transactions with ease.
        </p>

        {loading && <p className="loading-text">Loading your payments...</p>}
        {error && <p className="error-text">{error}</p>}

        {!loading && !error && payments.length === 0 && (
          <div className="empty-state">
            <p>No payments found</p>
            <small>Start by submitting your first international payment.</small>
          </div>
        )}

        {!loading && payments.length > 0 && (
          <div className="table-wrapper">
            <table className="modern-table">
              <thead>
                <tr>
                  <th>Amount</th>
                  <th>Currency</th>
                  <th>Payee Account</th>
                  <th>SWIFT Code</th>
                  <th>Status</th>
                  <th>Date</th>
                </tr>
              </thead>
              <tbody>
                {payments.map((p) => (
                  <tr key={p._id}>
                    <td>{p.amount}</td>
                    <td>{p.currency}</td>
                    <td>{p.payeeAccount}</td>
                    <td>{p.swiftCode}</td>
                    <td>
                      <span
                        className={`status-badge ${
                          p.status === "Pending" ? "pending" : "success"
                        }`}
                      >
                        {p.status || "Pending"}
                      </span>
                    </td>
                    <td>{p.createdAt ? new Date(p.createdAt).toLocaleDateString() : "-"}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
