// frontend/src/api/axiosInstance.js
import axios from "axios";

const axiosInstance = axios.create({
  baseURL: "https://localhost:5001/api",
  withCredentials: true, // send cookies with requests
});

// ðŸ§± Automatically fetch CSRF token from backend
axiosInstance.interceptors.request.use(async (config) => {
  // If we donâ€™t have a CSRF token, fetch one
  if (!axiosInstance.defaults.headers.common["X-CSRF-Token"]) {
    try {
      const res = await axios.get("https://localhost:5001/api/csrf-token", {
        withCredentials: true,
      });
      axiosInstance.defaults.headers.common["X-CSRF-Token"] = res.data.csrfToken;
    } catch (err) {
      console.error("Failed to fetch CSRF token:", err);
    }
  }
  return config;
});

export default axiosInstance;
