// src/pages/MakePayment.js
import { useState } from "react";
import { NavLink, useNavigate } from "react-router-dom";
import axios from "axios";
import "../AppStyles.css";
import { API_BASE } from "../config";

function MakePayment() {
  const [form, setForm] = useState({
    amount: "",
    currency: "",
    swiftCode: "",
    payeeAccount: "",
  });
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`${API_BASE}/api/payments`, form, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setMessage("Payment submitted successfully!");
    } catch (err) {
      setMessage("Error: " + (err.response?.data?.msg || err.message));
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    navigate("/login");
  };

  return (
    <div className="page-container">
      {/* TOP NAVBAR WITH LINKS + LOGOUT */}
      <div className="top-nav">
        <NavLink to="/dashboard" className="nav-link">My Dashboard</NavLink>
        <span> | </span>
        <NavLink to="/make-payment" className="nav-link">Make Payment</NavLink>
        <span> | </span>
        <button onClick={handleLogout} className="nav-button logout-btn">Logout</button>
      </div>

      <div className="card">
        <h2 className="title">Make a Payment</h2>
        <form className="form-layout" onSubmit={handleSubmit}>
          <input
            className="form-input"
            placeholder="Amount"
            name="amount"
            onChange={handleChange}
          />
          <input
            className="form-input"
            placeholder="Currency (e.g. USD)"
            name="currency"
            onChange={handleChange}
          />
          <input
            className="form-input"
            placeholder="SWIFT Code"
            name="swiftCode"
            onChange={handleChange}
          />
          <input
            className="form-input"
            placeholder="Payee Account"
            name="payeeAccount"
            onChange={handleChange}
          />
          <button className="primary-button" type="submit">
            Pay Now
          </button>
        </form>
        {message && <p className="message-text">{message}</p>}
      </div>
    </div>
  );
}

export default MakePayment;
