// frontend/src/pages/AdminDashboard.js
import { useEffect, useState } from "react";
import axios from "axios";
import { API_BASE } from "../config";
import "../AppStyles.css";

function AdminDashboard() {
  const [payments, setPayments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchPayments = async () => {
      try {
        // ⭐ CRITICAL FIX: Changed URL to the correct admin route
        const res = await axios.get(`${API_BASE}/api/admin/payments`, { 
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });

        // Ensure we always set an array
        setPayments(Array.isArray(res.data) ? res.data : []);
      } catch (err) {
        // Improved error handling to show what the backend returns
        setError(err.response?.data?.msg || "Failed to fetch payments");
      } finally {
        setLoading(false);
      }
    };

    fetchPayments();
  }, []);

  return (
    <div className="page-container light-theme">
      <div className="clean-card" style={{ width: "90%", maxWidth: "1000px" }}>
        <h2 className="title">All Payments (Admin View)</h2>

        {loading && <p>Loading payments...</p>}
        {error && <p style={{ color: "red" }}>{error}</p>}
        {!loading && payments.length === 0 && <p>No payments found.</p>}

        {payments.length > 0 && (
          <table className="payments-table">
            <thead>
              <tr>
                {/* Updated header to reflect the populated data */}
                <th>User Name</th>
                <th>User Account #</th>
                <th>Amount</th>
                <th>Currency</th>
                <th>Payee Account</th>
                <th>SWIFT Code</th>
                <th>Status</th>
                <th>Date</th>
              </tr>
            </thead>
            <tbody>
              {payments.map((p) => (
                <tr key={p._id}>
                  {/* ⭐ CRITICAL FIX: Access populated user data */}
                  <td>{p.userId?.fullName || 'N/A'}</td> 
                  <td>{p.userId?.accountNumber || 'N/A'}</td>
                  
                  <td>{p.amount}</td>
                  <td>{p.currency}</td>
                  <td>{p.payeeAccount}</td>
                  <td>{p.swiftCode}</td>
                  <td>{p.status}</td>
                  <td>{new Date(p.createdAt).toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default AdminDashboard;
