import { useState } from "react";
import axios from "axios";
import "../AppStyles.css";

function MakePayment() {
  const [form, setForm] = useState({
    amount: "",
    currency: "USD",
    payeeAccount: "",
    swiftCode: "",
  });
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      await axios.post("http://localhost:5000/api/payments", form, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMessage("✅ Payment submitted successfully!");
      setForm({ amount: "", currency: "USD", payeeAccount: "", swiftCode: "" });
    } catch (err) {
      setMessage("Error: " + (err.response?.data?.msg || err.message));
    }
  };

  return (
    <div className="page-container">
      <div className="card">
        <h2 className="title">Make an International Payment</h2>
        <p className="subtitle">Enter your payment details securely.</p>

        <form className="form-layout" onSubmit={handleSubmit}>
          <div className="form-group">
            <label className="form-label">Amount</label>
            <input
              className="form-input"
              type="number"
              name="amount"
              placeholder="Enter amount"
              value={form.amount}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label className="form-label">Currency</label>
            <select
              className="form-input"
              name="currency"
              value={form.currency}
              onChange={handleChange}
            >
              <option value="USD">USD - US Dollar</option>
              <option value="EUR">EUR - Euro</option>
              <option value="ZAR">ZAR - South African Rand</option>
              <option value="GBP">GBP - British Pound</option>
            </select>
          </div>

          <div className="form-group">
            <label className="form-label">Payee Account</label>
            <input
              className="form-input"
              type="text"
              name="payeeAccount"
              placeholder="Payee's account number"
              value={form.payeeAccount}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label className="form-label">SWIFT Code</label>
            <input
              className="form-input"
              type="text"
              name="swiftCode"
              placeholder="e.g. ABSAZAJJ"
              value={form.swiftCode}
              onChange={handleChange}
              required
            />
          </div>

          <button className="primary-button" type="submit">
            Pay Now
          </button>
        </form>

        {message && (
          <p className={`message-text ${message.startsWith("Error") ? "error" : "success"}`}>
            {message}
          </p>
        )}
      </div>
    </div>
  );
}

export default MakePayment;
