import { useEffect, useState } from "react";
import axios from "axios";
import "../AppStyles.css";
import { API_BASE } from "../config";

function AdminDashboard() {
  const [payments, setPayments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchPayments = async () => {
      try {
        const res = await axios.get(`${API_BASE}/api/payments/all`, {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
        });
        setPayments(res.data);
      } catch (err) {
        setError(err.response?.data?.msg || "Failed to fetch payments");
      } finally {
        setLoading(false);
      }
    };
    fetchPayments();
  }, []);

  return (
    <div className="page-container light-theme">
      <div className="clean-card" style={{width: "80%", maxWidth: "1000px"}}>
        <h2 className="dashboard-title">All Customer Payments</h2>
        {loading && <p className="info-text">Loading paymentsâ€¦</p>}
        {error && <p className="error-text">{error}</p>}

        {!loading && payments.length === 0 && <p>No payments yet.</p>}

        {!loading && payments.length > 0 && (
          <table className="modern-table">
            <thead>
              <tr>
                <th>Customer</th>
                <th>Amount</th>
                <th>Currency</th>
                <th>Payee Account</th>
                <th>SWIFT</th>
                <th>Date</th>
              </tr>
            </thead>
            <tbody>
              {payments.map(p => (
                <tr key={p._id}>
                  <td>{p.userName || p.userId}</td>
                  <td>{p.amount}</td>
                  <td>{p.currency}</td>
                  <td>{p.payeeAccount}</td>
                  <td>{p.swiftCode}</td>
                  <td>{new Date(p.createdAt).toLocaleDateString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default AdminDashboard;
