import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../AppStyles.css";
import { API_BASE } from "../config";

function Register() {
  const [form, setForm] = useState({ fullName: "", idNumber: "", accountNumber: "", password: "" });
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`${API_BASE}/api/auth/register`, form);
      setMessage("Registration successful! Please log in.");
      navigate("/login");
    } catch (err) {
      setMessage("Error: " + (err.response?.data?.msg || err.message));
    }
  };

  return (
    <div className="page-container">
      <div className="card">
        <h2 className="title">Create Account</h2>
        <form className="form-layout" onSubmit={handleSubmit}>
          <input className="form-input" placeholder="Full Name" name="fullName" onChange={handleChange} />
          <input className="form-input" placeholder="ID Number" name="idNumber" onChange={handleChange} />
          <input className="form-input" placeholder="Account Number" name="accountNumber" onChange={handleChange} />
          <input className="form-input" placeholder="Password" type="password" name="password" onChange={handleChange} />
          <button className="primary-button" type="submit">Register</button>
        </form>
        {message && <p className="message-text">{message}</p>}
        <div className="back-button-container">
            <button className="back-button" onClick={() => navigate(-1)}>
                <span className="arrow-icon">‚Üê</span>
                <span className="tooltiptext">Go back</span>
            </button>
        </div>
      </div>
    </div>
  );
}

export default Register;
