// frontend/src/App.js
import { BrowserRouter as Router, Routes, Route, NavLink, useNavigate, useLocation } from "react-router-dom";
// Note: We are removing useState and useEffect for isLoggedIn/role synchronization
// import { useState, useEffect } from "react";
import Home from "./pages/Home";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Dashboard from "./pages/Dashboard";
import MakePayment from "./pages/MakePayment";
import AdminDashboard from "./pages/AdminDashboard";
import CustomerNavbar from "./components/CustomerNavbar"; // IMPORTED
import "./App.css";

// --- The AuthStatus Component (FIXED) ---
const AuthStatus = () => {
    // FIX: Rely directly on localStorage for rendering state
    const isLoggedIn = !!localStorage.getItem("token");
    const role = localStorage.getItem("role");
    const navigate = useNavigate();
    const location = useLocation();

    // Removed useState and useEffect for simplicity and to fix timing issues.
    // The component will re-render correctly when navigation (which changes the URL/state) occurs.

    const handleLogout = () => {
        localStorage.removeItem("token");
        localStorage.removeItem("role");
        // No need to setIsLoggedIn(false) anymore
        navigate("/");
    };

    // --- NAVBAR HIDING LOGIC (CRITICAL FIX APPLIED) ---
    // Hide the navbar ONLY if the user is NOT logged in AND they are on the root path.
    // This allows the navbar to show for a logged-in user at /dashboard.
    if (location.pathname === "/" && !isLoggedIn) {
        return null;
    }

    // --- Conditional Content Rendering Based on Role ---
    let navContent;
    if (isLoggedIn) {
        if (role === "admin") {
            navContent = (
                <>
                    {/* Admin Link */}
                    <NavLink 
                        to="/admin-dashboard" 
                        className={({ isActive }) => (isActive ? "nav-link active" : "nav-link")}
                    >
                        All Payments
                    </NavLink>
                    <button onClick={handleLogout} className="nav-button logout-btn">Logout</button>
                </>
            );
        } else if (role === "customer") {
            // Customer Links: Renders the CustomerNavbar component
            navContent = <CustomerNavbar onLogout={handleLogout} />; 
        } else {
             // Fallback for logged-in but unknown role
            navContent = <button onClick={handleLogout} className="nav-button logout-btn">Logout</button>;
        }
    } else {
        // Unauthenticated Links (Shows Login/Register)
        navContent = (
            <>
                {/* Fallback to show Login/Register if no token */}
                <NavLink to="/login" className={({ isActive }) => (isActive ? "nav-link active" : "nav-link")}>Login</NavLink>
                <NavLink to="/register" className={({ isActive }) => (isActive ? "nav-link active" : "nav-link")}>Register</NavLink>
            </>
        );
    }
    // ---------------------------------------------------

    return (
        <nav className="navbar">
            <div className="nav-brand">International Payments</div>
            <div className="nav-links-container">
                {navContent}
            </div>
        </nav>
    );
};

function App() {
    return (
        <Router>
            <AuthStatus />
            <Routes>
                {/* Public Routes */}
                <Route path="/" element={<Home />} />
                <Route path="/login" element={<Login />} />
                <Route path="/register" element={<Register />} />

                {/* Customer Routes */}
                <Route path="/dashboard" element={<Dashboard />} />
                <Route path="/make-payment" element={<MakePayment />} />

                {/* Admin Route */}
                <Route path="/admin-dashboard" element={<AdminDashboard />} />
            </Routes>
        </Router>
    );
}

export default App;
