// frontend/src/pages/Register.js
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../AppStyles.css";
import { API_BASE } from "../config";

// For development: allow self-signed HTTPS (ignore SSL warnings)
import https from "https";
const httpsAgent = new https.Agent({ rejectUnauthorized: false });

function Register() {
  const [form, setForm] = useState({
    fullName: "",
    idNumber: "",
    accountNumber: "",
    password: ""
  });
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const userData = { ...form, role: "customer" };

      // POST request to backend (with HTTPS agent for dev)
      const res = await axios.post(`${API_BASE}/api/register`, userData, { httpsAgent });

      // Store token and role for auto-login
      if (res.data.token && res.data.user) {
        localStorage.setItem("token", res.data.token);
        localStorage.setItem("role", res.data.user.role);

        setMessage("Registration successful! Redirecting...");
        setTimeout(() => navigate("/dashboard"), 1200);
      } else {
        setMessage("Registration successful, please log in.");
        setTimeout(() => navigate("/login"), 1200);
      }
    } catch (err) {
      setMessage("Registration failed: " + (err.response?.data?.msg || err.message));
    }
  };

  return (
    <div className="page-container">
      <div className="card">
        <h2 className="title">Open Your Account</h2>
        <p className="subtitle">Please fill in your details to register.</p>

        <form className="form-layout" onSubmit={handleRegister}>
          {/* Full Name */}
          <div className="form-group">
            <label className="form-label">Full Name</label>
            <input
              className="form-input"
              name="fullName"
              placeholder="e.g. John Doe"
              value={form.fullName}
              onChange={handleChange}
              required
            />
          </div>

          {/* ID Number */}
          <div className="form-group">
            <label className="form-label">ID Number</label>
            <input
              className="form-input"
              name="idNumber"
              placeholder="e.g. 8001015002087"
              value={form.idNumber}
              onChange={handleChange}
              required
            />
          </div>

          {/* Account Number */}
          <div className="form-group">
            <label className="form-label">Account Number</label>
            <input
              className="form-input"
              name="accountNumber"
              placeholder="e.g. 12345678"
              value={form.accountNumber}
              onChange={handleChange}
              required
            />
          </div>

          {/* Password */}
          <div className="form-group">
            <label className="form-label">Password</label>
            <input
              className="form-input"
              type="password"
              name="password"
              placeholder="Enter a secure password"
              value={form.password}
              onChange={handleChange}
              required
            />
          </div>

          <button className="primary-button" type="submit">
            Register
          </button>
        </form>

        {message && <p className="message-text">{message}</p>}
      </div>
    </div>
  );
}

export default Register;
