import { useEffect, useState } from "react";
import axios from "axios";
import "../AppStyles.css";
import { API_BASE } from "../config";

function Dashboard() {
  const [payments, setPayments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchPayments = async () => {
      try {
        const res = await axios.get(`${API_BASE}/api/payments`, {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
        });

        console.log("Payments API response:", res.data);

        // ✅ Ensure we always get an array
        const data = Array.isArray(res.data) ? res.data : res.data?.payments;
        setPayments(data || []);
      } catch (err) {
        setError(err.response?.data?.msg || "Failed to fetch payments");
      } finally {
        setLoading(false);
      }
    };
    fetchPayments();
  }, []);

  return (
    <div className="page-container">
      <div className="card wide">
        <h2 className="title">Your Payment Dashboard</h2>
        <p className="subtitle">Track your international payments in real-time.</p>

        {loading && <p className="info-text">Loading payments...</p>}
        {error && <p className="message-text error">{error}</p>}

        {!loading && !error && payments.length === 0 && (
          <p className="info-text">No payments found.</p>
        )}

        {!loading && payments.length > 0 && (
          <table className="payments-table">
            <thead>
              <tr>
                <th>Amount</th>
                <th>Currency</th>
                <th>Payee Account</th>
                <th>SWIFT Code</th>
                <th>Status</th>
                <th>Date</th>
              </tr>
            </thead>
            <tbody>
              {payments.map((p) => (
                <tr key={p._id}>
                  <td>{p.amount}</td>
                  <td>{p.currency}</td>
                  <td>{p.payeeAccount}</td>
                  <td>{p.swiftCode}</td>
                  <td className="status success">{p.status || "Pending"}</td>
                  <td>{p.createdAt ? new Date(p.createdAt).toLocaleDateString() : "-"}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
