import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../AppStyles.css"; // Unified modern banking styles

function Login() {
  const [form, setForm] = useState({ accountNumber: "", password: "" });
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5000/api/auth/login", form);
      localStorage.setItem("token", res.data.token); // Save JWT
      setMessage("Login successful!");
      navigate("/dashboard");
    } catch (err) {
      setMessage("Error: " + (err.response?.data?.msg || err.message));
    }
  };

  return (
    <div className="page-container">
      <div className="card">
        <h2 className="title">Welcome Back</h2>
        <p className="subtitle">Log in to access your secure banking dashboard</p>
        
        <form className="form-layout" onSubmit={handleSubmit}>
          <div className="form-group">
            <label className="form-label" htmlFor="accountNumber">
              Account Number
            </label>
            <input
              className="form-input"
              type="text"
              name="accountNumber"
              id="accountNumber"
              placeholder="Enter your account number"
              value={form.accountNumber}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label className="form-label" htmlFor="password">
              Password
            </label>
            <input
              className="form-input"
              type="password"
              name="password"
              id="password"
              placeholder="Enter your password"
              value={form.password}
              onChange={handleChange}
              required
            />
          </div>

          <button className="primary-button" type="submit">
            Login
          </button>
        </form>

        {message && (
          <p className={`message-text ${message.startsWith("Error") ? "error" : "success"}`}>
            {message}
          </p>
        )}
      </div>
    </div>
  );
}

export default Login;
