import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../AppStyles.css";
import { API_BASE } from "../config";

function Login() {
  const [form, setForm] = useState({ accountNumber: "", password: "" });
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(`${API_BASE}/api/auth/login`, form);

      // ✅ Save token and role in localStorage
      localStorage.setItem("token", res.data.token);
      localStorage.setItem("role", res.data.role);

      setMessage("Login successful!");
      window.dispatchEvent(new Event("storage"));

      // ✅ Redirect based on role
      if (res.data.role === "admin") {
        navigate("/admin");
      } else {
        navigate("/dashboard");
      }
    } catch (err) {
      setMessage("Error: " + (err.response?.data?.msg || err.message));
    }
  };

  return (
    <div className="page-container">
      <div className="card">
        <h2 className="title">Welcome Back</h2>
        <p className="subtitle">Log in to access your secure banking dashboard.</p>
        <form className="form-layout" onSubmit={handleSubmit}>
          <div className="form-group">
            <label className="form-label">Account Number</label>
            <input
              className="form-input"
              name="accountNumber"
              value={form.accountNumber}
              onChange={handleChange}
            />
          </div>
          <div className="form-group">
            <label className="form-label">Password</label>
            <input
              className="form-input"
              type="password"
              name="password"
              value={form.password}
              onChange={handleChange}
            />
          </div>
          <button className="primary-button" type="submit">
            Login
          </button>
        </form>

        {message && <p className="message-text">{message}</p>}

        <div className="back-button-container">
          <button className="back-button" onClick={() => navigate(-1)}>
            <span className="arrow-icon">←</span>
            <span className="tooltiptext">Go back</span>
          </button>
        </div>
      </div>
    </div>
  );
}

export default Login;
