import { BrowserRouter as Router, Routes, Route, Link, useNavigate, useLocation } from "react-router-dom";
import { useState, useEffect } from "react";
import Home from "./pages/Home";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Dashboard from "./pages/Dashboard";
import MakePayment from "./pages/MakePayment";
import "./App.css"; // Assuming App.css contains your general styles

// Navbar component
const AuthStatus = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(!!localStorage.getItem("token"));
  const navigate = useNavigate();

  useEffect(() => {
    const handleStorageChange = () => {
      setIsLoggedIn(!!localStorage.getItem("token"));
    };
    window.addEventListener("storage", handleStorageChange);
    return () => window.removeEventListener("storage", handleStorageChange);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    setIsLoggedIn(false);
    navigate("/");
  };

  return (
    <nav className="navbar">
      <div className="nav-brand">CIP Portal</div>
      <div className="nav-links-container">
        {isLoggedIn ? (
          <>
            <Link to="/dashboard" className="nav-link">Dashboard</Link>
            <Link to="/make-payment" className="nav-link">Make Payment</Link>
            <button onClick={handleLogout} className="nav-button">Logout</button>
          </>
        ) : null}
      </div>
    </nav>
  );
};

function App() {
  const location = useLocation();

  // Hide navbar on Home, Login, Register
  const hideNavbarOn = ["/", "/login", "/register"];

  return (
    <>
      {!hideNavbarOn.includes(location.pathname) && <AuthStatus />}
      <div className="main-content">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/dashboard" element={<Dashboard />} />
          <Route path="/make-payment" element={<MakePayment />} />
        </Routes>
      </div>
    </>
  );
}

// Wrap App with Router
export default function AppWrapper() {
  return (
    <Router>
      <App />
    </Router>
  );
}
