import { useEffect, useState } from "react";
import axios from "axios";
import { API_BASE } from "../config";
import "../AppStyles.css";

function AdminDashboard() {
  const [payments, setPayments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchAllPayments = async () => {
      try {
        const res = await axios.get(`${API_BASE}/api/admin/payments`, {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        setPayments(res.data);
      } catch (err) {
        setError(err.response?.data?.msg || "Failed to fetch payments");
      } finally {
        setLoading(false);
      }
    };

    fetchAllPayments();
  }, []);

  const handleVerify = async (id) => {
    try {
      await axios.put(`${API_BASE}/api/admin/payments/${id}/verify`, {}, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setPayments((prev) =>
        prev.map((p) => (p._id === id ? { ...p, status: "Verified" } : p))
      );
    } catch (err) {
      alert("Error verifying payment");
    }
  };

  return (
    <div className="dashboard-container light-theme">
      <div className="dashboard-card">
        <h2 className="dashboard-title">Employee Payment Portal</h2>
        <p className="dashboard-subtitle">Verify and forward international payments</p>

        {loading && <p className="loading-text">Loading...</p>}
        {error && <p className="error-text">{error}</p>}

        {!loading && payments.length === 0 && (
          <p className="empty-state">No payments found</p>
        )}

        {!loading && payments.length > 0 && (
          <table className="modern-table">
            <thead>
              <tr>
                <th>Customer</th>
                <th>Amount</th>
                <th>Currency</th>
                <th>Payee Account</th>
                <th>SWIFT Code</th>
                <th>Status</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {payments.map((p) => (
                <tr key={p._id}>
                  <td>{p.userId}</td>
                  <td>{p.amount}</td>
                  <td>{p.currency}</td>
                  <td>{p.payeeAccount}</td>
                  <td>{p.swiftCode}</td>
                  <td>{p.status}</td>
                  <td>
                    {p.status !== "Verified" && (
                      <button
                        className="primary-button small"
                        onClick={() => handleVerify(p._id)}
                      >
                        Verify
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default AdminDashboard;
