// frontend/src/pages/AdminDashboard.js
import { useEffect, useState } from "react";
import axios from "axios";
import { API_BASE } from "../config";
import "../AppStyles.css";

function AdminDashboard() {
  const [payments, setPayments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchPayments = async () => {
      // Reset error and set loading before starting the fetch
      setError("");
      setLoading(true);

      try {
        const token = localStorage.getItem("token");

        if (!token) {
          throw new Error("Missing authentication token.");
        }

        // CORRECTED URL: Calling the secure admin endpoint
        const res = await axios.get(`${API_BASE}/api/admin/payments`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        
        // FIX: Ensure payments is ALWAYS an array to avoid the .map error
        if (Array.isArray(res.data)) {
          setPayments(res.data);
        } else {
          console.error("Backend returned non-array data:", res.data);
          setPayments([]); // Fallback to an empty array
        }

      } catch (err) {
        console.error("API Call Error:", err.response || err);
        // Extract specific error message or provide a fallback
        setError(err.response?.data?.msg || err.message || "Could not connect to server or fetch payments.");
        setPayments([]); // CRITICAL: Reset to an empty array on error
      } finally {
        // Always stop loading, regardless of success or failure
        setLoading(false);
      }
    };

    fetchPayments();
  }, []); 

  return (
    <div className="page-container light-theme">
      <div className="clean-card" style={{ width: "90%", maxWidth: "1000px" }}>
        <h2 className="title">All Payments (Admin View)</h2>

        {loading && <p>Loading payments...</p>}
        {error && <p style={{ color: "red" }}>{error}</p>}
        {!loading && payments.length === 0 && !error && <p>No payments found.</p>}

        {payments.length > 0 && (
          <table className="payments-table">
            <thead>
              <tr>
                <th>User Name</th>
                <th>Account #</th>
                <th>Amount</th>
                <th>Currency</th>
                <th>Payee Account</th>
                <th>SWIFT Code</th>
                <th>Status</th>
                <th>Date</th>
              </tr>
            </thead>
            <tbody>
              {payments.map((p) => (
                <tr key={p._id}>
                  {/* Access populated user data safely */}
                  <td>{p.userId?.fullName || 'N/A'}</td> 
                  <td>{p.userId?.accountNumber || 'N/A'}</td>
                  <td>{p.amount}</td>
                  <td>{p.currency}</td>
                  <td>{p.payeeAccount}</td>
                  <td>{p.swiftCode}</td>
                  <td>{p.status}</td>
                  <td>{new Date(p.createdAt).toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default AdminDashboard;
