// frontend/src/pages/AdminDashboard.js
import { useEffect, useState } from "react";
import axios from "axios";
import { API_BASE } from "../config";
import "../AppStyles.css";

const formatCurrency = (amount, currency) => {
  try {
    return new Intl.NumberFormat("en-US", {
      style: "currency",
      currency: currency || "USD",
      minimumFractionDigits: 2,
    }).format(amount);
  } catch {
    return `${amount} ${currency || ""}`;
  }
};

const getStatusClass = (status) => {
  switch (status.toLowerCase()) {
    case "completed":
      return "status-completed";
    case "pending":
      return "status-pending";
    case "verified":
      return "status-verified";
    case "failed":
      return "status-failed";
    default:
      return "status-default";
  }
};

function AdminDashboard() {
  const [payments, setPayments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [selectedForSwift, setSelectedForSwift] = useState([]);

  const token = localStorage.getItem("token");

  // Fetch all payments for admin
  useEffect(() => {
    const fetchPayments = async () => {
      setError("");
      setLoading(true);
      try {
        if (!token) throw new Error("Missing authentication token.");
        const res = await axios.get(`${API_BASE}/api/admin/payments`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setPayments(Array.isArray(res.data) ? res.data : []);
      } catch (err) {
        setError(
          err.response?.data?.msg ||
            err.message ||
            "Could not connect to server or fetch payments."
        );
        setPayments([]);
      } finally {
        setLoading(false);
      }
    };

    fetchPayments();
  }, [token]);

  // Handle verify action
  const handleVerify = async (paymentId) => {
    try {
      await axios.patch(
        `${API_BASE}/api/admin/payments/${paymentId}/verify`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setPayments((prev) =>
        prev.map((p) =>
          p._id === paymentId ? { ...p, status: "Verified" } : p
        )
      );
    } catch (err) {
      alert("Error verifying payment: " + (err.response?.data?.msg || err.message));
    }
  };

  // Handle selecting for SWIFT submission
  const toggleSelect = (paymentId) => {
    setSelectedForSwift((prev) =>
      prev.includes(paymentId)
        ? prev.filter((id) => id !== paymentId)
        : [...prev, paymentId]
    );
  };

  // Handle submit to SWIFT
  const handleSubmitToSwift = async () => {
    try {
      await axios.post(
        `${API_BASE}/api/admin/payments/submit-swift`,
        { paymentIds: selectedForSwift },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setPayments((prev) =>
        prev.map((p) =>
          selectedForSwift.includes(p._id)
            ? { ...p, status: "Completed" }
            : p
        )
      );
      setSelectedForSwift([]);
      alert("Selected payments submitted to SWIFT!");
    } catch (err) {
      alert("Error submitting to SWIFT: " + (err.response?.data?.msg || err.message));
    }
  };

  return (
    <div className="dashboard-layout">
      <div className="data-card">
        <h2 className="card-title">
          All Payments <span className="title-count">({payments.length})</span>
        </h2>

        <hr className="divider" />

        {loading && (
          <p className="status-message loading-message">Loading payments...</p>
        )}
        {error && <p className="status-message error-message">{error}</p>}
        {!loading && payments.length === 0 && !error && (
          <p className="status-message no-data-message">No payments found.</p>
        )}

        {payments.length > 0 && (
          <>
            <div className="table-wrapper">
              <table className="payments-table">
                <thead>
                  <tr>
                    <th>Select</th>
                    <th>User</th>
                    <th>Account #</th>
                    <th className="th-right">Amount</th>
                    <th>Payee Account</th>
                    <th>SWIFT Code</th>
                    <th>Status</th>
                    <th>Date</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {payments.map((p) => (
                    <tr key={p._id}>
                      <td>
                        <input
                          type="checkbox"
                          disabled={p.status.toLowerCase() !== "verified"}
                          checked={selectedForSwift.includes(p._id)}
                          onChange={() => toggleSelect(p._id)}
                        />
                      </td>
                      <td>
                        <div className="user-details">
                          <span className="user-name">
                            {p.sender?.fullName || "N/A"}
                          </span>
                          <span className="user-account-number">
                            {p.sender?.accountNumber || "N/A"}
                          </span>
                        </div>
                      </td>
                      <td>{p.sender?.accountNumber || "N/A"}</td>
                      <td className="td-right">
                        {formatCurrency(p.amount, p.currency)}
                      </td>
                      <td>{p.payeeAccount}</td>
                      <td>{p.swiftCode}</td>
                      <td>
                        <span
                          className={`status-badge ${getStatusClass(p.status)}`}
                        >
                          {p.status}
                        </span>
                      </td>
                      <td>{new Date(p.createdAt).toLocaleDateString()}</td>
                      <td>
                        {p.status.toLowerCase() === "pending" && (
                          <button
                            className="verify-btn"
                            onClick={() => handleVerify(p._id)}
                          >
                            Verify
                          </button>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            <button
              className="submit-swift-btn"
              onClick={handleSubmitToSwift}
              disabled={selectedForSwift.length === 0}
            >
              Submit Selected to SWIFT
            </button>
          </>
        )}
      </div>
    </div>
  );
}

export default AdminDashboard;
