import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { API_BASE } from "../config";

function Register() {
    const [form, setForm] = useState({ username: "", password: "", email: "" });
    const [message, setMessage] = useState("");
    const navigate = useNavigate();

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleRegister = async (e) => {
        e.preventDefault();
        try {
            const userData = { ...form, role: "customer" }; // Set role to "customer" by default
            const res = await axios.post(`${API_BASE}/api/register`, userData);
            localStorage.setItem("token", res.data.token);
            localStorage.setItem("role", res.data.user.role); // Store the role from the response
            navigate("/dashboard");
        } catch (err) {
            setMessage("Registration failed: " + err.response?.data?.msg || err.message);
        }
    };

    return (
        <div>
            <form onSubmit={handleRegister}>
                <input name="username" placeholder="Username" onChange={handleChange} />
                <input name="email" type="email" placeholder="Email" onChange={handleChange} />
                <input name="password" type="password" placeholder="Password" onChange={handleChange} />
                <button type="submit">Register</button>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
}

export default Register;
