// frontend/src/pages/Register.js
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../AppStyles.css";
import { API_BASE } from "../config";

function Register() {
  const [form, setForm] = useState({
    fullName: "",
    idNumber: "",
    accountNumber: "",
    password: ""
  });
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      // Use userData with only form data, relying on Mongoose for 'role'
      const userData = { ...form }; 

      const res = await axios.post(`${API_BASE}/api/auth/register`, userData);

      // ðŸ›‘ CRITICAL DEBUGGING & ROBUSTNESS CHECK ðŸ›‘
      console.log("Registration Success Response:", res.data); // LOG THE RESPONSE

      // Safely access nested properties
      const token = res.data.token;
      const userRole = res.data.user?.role; // Use optional chaining for safety

      if (!token || !userRole) {
          // If the token or role is missing despite a 200 status, throw an error
          throw new Error("Server response missing token or user role.");
      }
      
      // Store token and role for auto-login
      localStorage.setItem("token", token);
      localStorage.setItem("role", userRole);

      setMessage("Registration successful! Redirecting...");
      setTimeout(() => navigate("/dashboard"), 1200);

    } catch (err) {
      // Handle server-side errors (e.g., 400, 500) or network errors
      const errorMessage = err.response?.data?.msg 
                         || err.response?.statusText 
                         || err.message;
      setMessage("Registration failed: " + errorMessage);
    }
  };

  return (
    // ... (rest of the component remains the same)
    <div className="page-container">
      <div className="card">
        <h2 className="title">Open Your Account</h2>
        <p className="subtitle">Please fill in your details to register.</p>

        <form className="form-layout" onSubmit={handleRegister}>
          {/* ... (input fields) */}
          <div className="form-group">
            <label className="form-label">Full Name</label>
            <input
              className="form-input"
              name="fullName"
              placeholder="e.g. John Doe"
              value={form.fullName}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label className="form-label">ID Number</label>
            <input
              className="form-input"
              name="idNumber"
              placeholder="e.g. 8001015002087"
              value={form.idNumber}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label className="form-label">Account Number</label>
            <input
              className="form-input"
              name="accountNumber"
              placeholder="e.g. 12345678"
              value={form.accountNumber}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label className="form-label">Password</label>
            <input
              className="form-input"
              type="password"
              name="password"
              placeholder="Enter a secure password"
              value={form.password}
              onChange={handleChange}
              required
            />
          </div>

          <button className="primary-button" type="submit">
            Register
          </button>
        </form>

        {message && <p className="message-text">{message}</p>}
      </div>
    </div>
  );
}

export default Register;
