// frontend/src/api/axiosInstance.js
import axios from "axios";

const axiosInstance = axios.create({
  baseURL: "https://localhost:5001/api", // ✅ matches backend HTTPS server
  withCredentials: true, // ✅ send httpOnly cookies with each request
});

// --- Helper to fetch CSRF token ---
const getCsrfToken = async () => {
  try {
    const res = await axios.get("https://localhost:5001/api/csrf-token", {
      withCredentials: true,
    });
    return res.data.csrfToken;
  } catch (err) {
    console.error("❌ Failed to get CSRF token:", err);
    return null;
  }
};

// --- Interceptor: attach CSRF token for POST, PUT, DELETE ---
axiosInstance.interceptors.request.use(
  async (config) => {
    const needsCsrf = ["post", "put", "delete"].includes(config.method);
    if (needsCsrf) {
      const csrfToken = await getCsrfToken();
      if (csrfToken) config.headers["CSRF-Token"] = csrfToken;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

// --- Optional: central error logging ---
axiosInstance.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 403 && error.response?.data?.msg?.includes("CSRF")) {
      console.warn("⚠️ CSRF token invalid or missing.");
    }
    return Promise.reject(error);
  }
);

export default axiosInstance;
