import { useEffect, useState } from "react";
import axios from "axios";
import { API_BASE } from "../config";
import "../AppStyles.css";

function AdminDashboard() {
  const [payments, setPayments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchPayments = async () => {
      try {
        const res = await axios.get(`${API_BASE}/api/payments/all`, {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });

        console.log("üîé Admin API response:", res.data);

        // ‚úÖ Ensure we set an array
        const data = Array.isArray(res.data) ? res.data : [];
        setPayments(data);
      } catch (err) {
        console.error("‚ùå Error fetching payments:", err.response?.data || err.message);
        setError(err.response?.data?.msg || "Failed to fetch payments");
      } finally {
        setLoading(false);
      }
    };

    fetchPayments();
  }, []);

  return (
    <div className="page-container light-theme">
      <div className="clean-card wide">
        <h2 className="title">All Payments (Admin View)</h2>

        {loading && <p className="info-text">Loading payments...</p>}
        {error && <p className="message-text error">{error}</p>}

        {!loading && !error && payments.length === 0 && (
          <p className="info-text">No payments found.</p>
        )}

        {!loading && payments.length > 0 && (
          <table className="payments-table">
            <thead>
              <tr>
                <th>Customer</th>
                <th>Amount</th>
                <th>Currency</th>
                <th>Payee Account</th>
                <th>SWIFT Code</th>
                <th>Status</th>
                <th>Date</th>
              </tr>
            </thead>
            <tbody>
              {payments.map((p) => (
                <tr key={p._id}>
                  <td>{p.userId}</td>
                  <td>{p.amount}</td>
                  <td>{p.currency}</td>
                  <td>{p.payeeAccount}</td>
                  <td>{p.swiftCode}</td>
                  <td>{p.status}</td>
                  <td>{new Date(p.createdAt).toLocaleDateString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default AdminDashboard;
